def phi(n):
    result = n; temp = n; p = 2
    while p * p <= temp:
        if temp % p == 0:
            while temp % p == 0: temp //= p
            result -= result // p
        p += 1
    if temp > 1: result -= result // temp
    return result
answer = sum(phi(k) for k in range(1, 51)); assert answer == 774, f'Got {answer}'; print(answer)
